mapper.select:
select a.* from activities a inner join participate p on a.activity_id = p.activity_id
where CURRENT_TIME >= a.end_time and p.user_id = #{user_id}
order by a.start_time desc
limit #{offset},#{page_size};

select a.* from activities a inner join participate p on a.activity_id = p.activity_id
where CURRENT_TIME >= a.end_time and  a.start_time BETWEEN #{start} and #{end} and p.user_id = #{user_id}
order by a.start_time desc
